local teams = { 	{ "Administração", 255, 255, 255 },	{ "Criminoso", 237, 79, 79 },	{ "Policial", 0, 100, 255 },	{ "Civilizante", 255, 255, 0 },	{ "Emergencia", 146, 221, 249 },	{ "Desempregado", 255, 210, 6 },	{ "Convidado", 172, 211, 115 },}local lawTeams = { 	['Policial'] = true}function onJoin(player) setPlayerTeam(player, getTeamFromName ( "Convidado" ) )endfunction timer()setTimer(onJoin,1000,1,source)endaddEventHandler("onPlayerJoin", getRootElement(), timer)function nicks (thePlayer)local name = getPlayerName(thePlayer)local nick = string.gsub(name, "#%x%x%x%x%x%x", "")setPlayerName (thePlayer, nick)endfunction timer1()setTimer(nicks,1000,1,source)endaddEventHandler("onPlayerJoin", getRootElement(), timer1)addEventHandler("onClientPlayerChangeNick", getRootElement(), timer1)local team = { }for i, v in ipairs ( teams ) do	team[v[1]] = createTeam ( unpack ( v ) )endfunction setTeam ( p, tem ) 	if ( p and getElementType ( p ) == 'player' and tem and type ( tem ) == 'string' ) then		for i, v in ipairs ( teams ) do 			if ( v[1] == tem ) then				return setPlayerTeam ( p, getTeamFromName ( v[1] ) )			end		end	end	return falseendaddEventHandler ( "onResourceStop", root, function ( )	for i, v in ipairs ( getElementsByType ( 'player' ) ) do 		if ( getPlayerTeam ( v ) ) then			setElementData ( v, "VDBGPlayers:SavedTeam", getTeamName ( getPlayerTeam ( v ) ) )		end	endend )addEventHandler ( 'onResourceStart', resourceRoot, function ( )	for i, v in ipairs ( getElementsByType ( 'player' ) ) do 		local t = getElementData ( v, 'VDBGPlayers:SavedTeam' )		if t and getTeamFromName ( t ) then 			setPlayerTeam ( v, getTeamFromName ( t ) )		else			setPlayerTeam ( v, getTeamFromName ( t, "Convidado" ) )		end	endend )function isTeamLaw ( team )	local team = tostring ( team )	if ( lawTeams[team] ) then		return true	end	return falseend